var shotgunReload=function(){var t=[].slice.call(document.getElementsByClassName("shotgun"));t.forEach(function(t){if((" "+t.className+" ").indexOf(" shot ")===-1){t.classList.add("shot");var o=t.offsetWidth,e=t.offsetHeight;t.style.position="relative";var s=[].slice.call(t.children);s.forEach(function(t){t.style.position="absolute",t.style.left=Math.random()*o-t.offsetWidth/2,t.style.top=Math.random()*e-t.offsetHeight/2})}})};window.onload=function(){shotgunReload()};